import React from "react";
import Logo from "../Assets/new_logo1.png";

function Sidebar() {
  return (
    <div className="bg-[#000000] w-[23vw] h-[100vh] text-white flex flex-col justify-center items-center">
      <img
        src={Logo}
        alt="Logo"
        style={{ width: "50%", height: "auto", paddingTop: "6%", maxWidth: "150px" }}
      />
      <div className="flex flex-col justify-center items-center flex-grow">
        <button style={buttonStyle}>Home</button>
        <button style={buttonStyle}>Datasets</button>
        <button style={buttonStyle}>Services</button>
        <button style={buttonStyle}>About Us</button>
      </div>
      <style jsx>{`
        @media (max-width: 600px) {
          button {
            width: 100%;
          }
        }
      `}</style>
    </div>
  );
}

const buttonStyle = {
  backgroundColor: "#333333",
  border: "none",
  borderRadius: "2xl",
  padding: "1.5rem",
  width: "80%",
  margin: "0.5rem 0",
  fontSize: "1rem",
};

export default Sidebar;
